litex_example:
have a, b R
have set A = {x R: a <= x < b}
have set B = {x R: a < x <= b}

let a, b R: a < b
have set interval_a_b = {x R : a < x, x < b}
forall x R: a < x, x < b => x $in interval_a_b

let a, b R: a < b
have set closed_interval_a_b = {x R : a <= x, x <= b}
forall x R: a < x, x < b => x $in closed_interval_a_b

have set R_pos = {x R: x > 0}
forall x R_pos, y R_pos: x > 0, y > 0, x * y > 0, x * y >= 0, sqrt(x * y) > 0, sqrt(x * y) $in R_pos
have fn geometric_mean(a, b R_pos) R_pos = sqrt(a * b)
